    private void butaddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butaddActionPerformed
        // TODO add your handling code here:
        if(txttittle.getText().equals("")||txtdirector.getText().equals("")||txtcompany.getText().equals("")||txtyear.getText().equals("")){
        JOptionPane.showMessageDialog(this, "Incomplete Data");
        }else{
        String data[] = {txttittle.getText(),txtdirector.getText(),txtcompany.getText(),txtyear.getText()};
        DefaultTableModel tblModel = (DefaultTableModel)jTable1.getModel();
        tblModel.addRow(data);
        JOptionPane.showMessageDialog(this, "Added data sucsessful");
        
        txttittle.setText("");
        txtdirector.setText("");
        txtcompany.setText("");
        txtyear.setText("");
      
        }
    }//GEN-LAST:event_butaddActionPerformed

