    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        // TODO add your handling code here:
         DefaultTableModel tblModel = (DefaultTableModel)jTable1.getModel();
         String tbltittle = tblModel.getValueAt(jTable1.getSelectedRow(),0).toString();
         String tbldirector = tblModel.getValueAt(jTable1.getSelectedRow(),1).toString();
         String tblproduction = tblModel.getValueAt(jTable1.getSelectedRow(),2).toString();
         String tblyear = tblModel.getValueAt(jTable1.getSelectedRow(),3).toString();
         
        txttittle.setText(tbltittle);
        txtdirector.setText(tbldirector);
        txtcompany.setText(tblproduction);
        txtyear.setText(tblyear); 
         
    }//GEN-LAST:event_jTable1MouseClicked

    private void butadd1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butadd1ActionPerformed
        // TODO add your handling code here:
         DefaultTableModel tblModel = (DefaultTableModel)jTable1.getModel();
         if(jTable1.getSelectedRowCount() == 1){
             tblModel.removeRow(jTable1.getSelectedRow());
         }else{
         if(jTable1.getRowCount() == 1){
             JOptionPane.showMessageDialog(this, "Empty table");
         }else{
             JOptionPane.showMessageDialog(this, "Select one row to remove"); 
         }
         }
    }//GEN-LAST:event_butadd1ActionPerformed

    private void btnupdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnupdateActionPerformed
        // TODO add your handling code here:
        DefaultTableModel tblModel = (DefaultTableModel)jTable1.getModel();
        if(jTable1.getSelectedRowCount() == 1){
            String tittle = txttittle.getText();
            String director = txtdirector.getText();
            String company = txtcompany.getText();
            String year = txtyear.getText();
            
            tblModel.setValueAt(tittle,jTable1.getSelectedRow(),0);
            tblModel.setValueAt(director,jTable1.getSelectedRow(),1);
            tblModel.setValueAt(company,jTable1.getSelectedRow(),2);
            tblModel.setValueAt(year,jTable1.getSelectedRow(),3);
            
           JOptionPane.showMessageDialog(this, "Update sucsessful");         
        }else{
            if(jTable1.getRowCount()==0){
            JOptionPane.showMessageDialog(this, "Empty content"); 
            }else{
            JOptionPane.showMessageDialog(this, "Select one row to update"); 
            }
        }
    }//GEN-LAST:event_btnupdateActionPerformed

